import Axios from 'axios';

const domain = 'https://ncn2019.herokuapp.com/api';

export const fetchArticles = (url) => {
  return Axios.get(`https://ncn2019.herokuapp.com/api/articles/${url}`);
};

export const fetchComments = (url) => {
  return Axios.get(
    `https://ncn2019.herokuapp.com/api/articles/${url}/comments`
  );
};
