import Axios from 'axios';

const domain = 'https://ncn2019.herokuapp.com/api/';

export const fetchArticles = (articleId) => {
  return Axios.get(`${domain}articles/${articleId}`);
};

export const fetchComments = (articleId) => {
  return Axios.get(`${domain}articles/${articleId}/comments`);
};

export const patchVotes = (path, vote) => {
  return Axios.patch(`${domain}${path}`, {
    inc_votes: vote
  });
};
