import React, { Component } from 'react';
import deleteComment from '../utilities';
import Axios from 'axios';

class RemoveComment extends Component {
  render() {
    return (
      <div>
        <button
          onClick={() =>
            this.removeComment(this.props.path, this.props.comment)
          }
        >
          delete comment
        </button>
      </div>
    );
  }

  removeComment(path, comment) {
    //   Axios.delete(url).then((data) => this.props.removeDeletedComment(comment));
    // }
    deleteComment(path).then((data) =>
      this.props.removeDeletedComment(comment)
    );
  }
}

export default RemoveComment;
