import React, { Component } from 'react';
import Axios from 'axios';

class UserAvatar extends Component {
  state = {
    avatar_url: true,
  };
  render() {
    return (
      <div>
        {this.state.avatar_url && (
          <div>
            <img
              src={
                'https://cdn.pixabay.com/photo/2014/10/01/10/44/hedgehog-468228_960_720.jpg'
              }
              alt=""
            />
            {/* <img src={this.state.avatar_url} alt="" /> */}
          </div>
        )}
      </div>
    );
  }
  componentDidMount() {
    Axios.get(
      `https://ncn2019.herokuapp.com/api/users/${this.props.author}`
    ).then(({ data }) => this.setState({ avatar_url: data.avatar_url }));
  }
}

export default UserAvatar;
