import Axios from 'axios';

const domain = 'https://ncn2019.herokuapp.com/api/';

export const fetchArticles = (path) => {
  return Axios.get(`${domain}articles/${path}`);
};

export const fetchComments = (articleID) => {
  return Axios.get(`${domain}articles/${articleID}/comments`);
};

export const postComment = (articleID, username, commentInput) => {
  return Axios.post(`${domain}articles/${articleID}/comments`, {
    author: username,
    body: commentInput
  });
};

export const deleteComment = (commentID) => {
  return Axios.delete(`${domain}/comments/${commentID}`);
};

export const patchVotes = (path, vote) => {
  return Axios.patch(`${domain}${path}`, {
    inc_votes: vote
  });
};
