import React, { Component } from 'react';
import { Router } from '@reach/router';
import './App.css';
import Header from './Components/Header';
import Login from './Components/Login';
import NavBar from './Components/NavBar';
import Topics from './Components/Topics';
import Topic from './Components/Topic';
import Users from './Components/Users';
import ArticleList from './Components/ArticleList';
import Article from './Components/Article';
import LoggedInUser from './Components/LoggedInUser';

class App extends Component {
  state = {
    loggedInUser: null,
    sortBy: 'hottest'
  };

  render() {
    return (
      <div className="App">
        <Header />
        {!this.state.loggedInUser && <Login loginUser={this.loginUser} />}
        {this.state.loggedInUser && (
          <LoggedInUser
            loggedInUser={this.state.loggedInUser}
            logoutUser={this.logoutUser}
          />
        )}
        <NavBar />
        <Router>
          <ArticleList path="/articles" default />
          <Article
            path="/articles/:article_id"
            loggedInUser={this.state.loggedInUser}
          />
          <Topics path="/topics" />
          <Topic />
          <Users path="/users" />
        </Router>
      </div>
    );
  }

  loginUser = (userData) => {
    this.setState({ loggedInUser: userData });

    sessionStorage.setItem(
      'loggedInUser',
      JSON.stringify(this.state.loggedInUser)
    );

    console.log(sessionStorage);

    // const sessionData = JSON.parse(sessionStorage.getItem('loggedInUser'));
  };

  logoutUser = () => {
    this.setState({ loggedInUser: null });

    sessionStorage.removeItem('loggedInUser');
    console.log(sessionStorage);
  };
}

export default App;
