import React, { Component } from 'react';
import Axios from 'axios';
import UserAvatar from './UserAvatar';
import Vote from './Vote';
import AddComment from './CommentsSection';
import CommentsSection from './CommentsSection';

class Article extends Component {
  state = { article: null, comments: null, avatar_url: '' };
  render() {
    console.log(this.state.avatar_url);
    return (
      <div>
        {this.state.article && this.state.comments && (
          <div>
            <UserAvatar username={this.state.article.author} />
            <h1>{this.state.article.title}</h1>
            <h2>by {this.state.article.author}</h2>
            <p>{this.state.article.body}</p>
            <Vote
              votes={this.state.article.votes}
              url={`https://ncn2019.herokuapp.com/api/articles/${
                this.state.article.article_id
              }`}
            />
            <CommentsSection article_id={this.state.article.article_id} />
          </div>
        )}
      </div>
    );
  }
  componentDidMount() {
    Axios.get(
      `https://ncn2019.herokuapp.com/api/articles/${this.props.article_id}`
    ).then(({ data }) => this.setState({ article: data.article }));

    Axios.get(
      `https://ncn2019.herokuapp.com/api/articles/${
        this.props.article_id
      }/comments`
    ).then(({ data }) => this.setState({ comments: data.comments }));

    this.state.user_id &&
      Axios.get(
        `https://ncn2019.herokuapp.com/api/users/${this.props.article.author}`
      ).then(({ data }) => this.setState({ avatar_url: data.avatar_url }));
  }
}

export default Article;
