import React, { Component } from 'react';
import Axios from 'axios';

class Article extends Component {
  state = { article: null, comments: null };
  render() {
    console.log(this.props.article_id);
    console.log(this.state.article);
    return (
      <div>
        {this.state.article && (
          <div>
            something
            <h1>{this.state.article.title}</h1>
            <h2>by {this.state.article.author}</h2>
            <p>{this.state.article.body}</p>
          </div>
        )}
      </div>
    );
  }
  componentDidMount() {
    const url = `https://ncn2019.herokuapp.com/api/articles/${
      this.props.article_id
    }`;
    Axios.get(
      `https://ncn2019.herokuapp.com/api/articles/${this.props.article_id}`
    )
      .then(({ data }) => this.setState({ article: data.article }))
      .then(console.log(url));

    Axios.get(url)
      .then(({ data }) => this.setState({ article: data.article }))
      .then(console.log(url));
  }
}

export default Article;
