import Axios from 'axios';

const domain = 'https://ncn2019.herokuapp.com/api/';

export const fetchArticles = (url) => {
  return Axios.get(`${domain}articles/${url}`);
};

export const fetchComments = (url) => {
  return Axios.get(`${domain}articles/${url}/comments`);
};

export const patchVotes = (url, vote) => {
  return Axios.patch(`${domain}${url}`, {
    inc_votes: vote
  });
};
