import React, { Component } from 'react';
import Axios from 'axios';

class AddComment extends Component {
  state = { commentInput: '', commentAuthor: null }; // author is hardcoded for now

  render() {
    return (
      <div>
        {
          <div>
            <form
              onSubmit={(event) => {
                event.preventDefault();
                this.submitComment();
              }}
            >
              <input
                onChange={(event) =>
                  this.setState({ commentInput: event.target.value })
                }
              />
              <button>comment</button>
            </form>
          </div>
        }
      </div>
    );
  }

  componentDidMount() {
    this.setState({ commentAuthor: this.props.loggedInUser.username });
  }

  submitComment = () => {
    // this.setState({
    //   commentAuthor: JSON.parse(sessionStorage.getItem('loggedInUser'))
    //     .username,
    // });

    // console.log(JSON.parse(sessionStorage.getItem('loggedInUser')).username);

    console.log(this.state.commentAuthor);

    if (this.state.commentAuthor) {
      Axios.post(
        `https://ncn2019.herokuapp.com/api/articles/${
          this.props.article_id
        }/comments`,
        {
          author: this.state.commentAuthor,
          body: this.state.commentInput,
        }
      ).then(({ data }) => this.props.showNewComment(data.comment));
    } else {
      console.log('a bad thing happened....');
    }
  };
}

export default AddComment;
